%tes7sub.mclearclcpkg load imageL = imread('Lena.tiff');[m,n,k] = size(L);L = imread('Lena.tiff');logo = imread('logo.bmp');logonew = imresize(logo,2.56);logobi = im2bw(logonew);watermark = [logobi logobi logobi logobi;logobi logobi logobi logobi;logobi logobi logobi logobi;logobi logobi logobi logobi;];B = L(:,:,3);B_wa = bitset(B,1,watermark);newL = L;newL(:,:,3) = B_wa;psnr_val = psnr(L,newL);imwrite(newL,'Lena_b.bmp');J = imread('Lena_bb.bmp');B = J(:,:,3);B_ex = bitget(B,1);figure;imshow(B_ex,[])corr2(B_ex,watermark);